function checkEmail(e){return/^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/.test(String(e).toLowerCase())}function ucwords(e){return(e+"").replace(/^([a-z])|\s+([a-z])/g,function(e){return e.toUpperCase()})}document.getElementById("send-mail").addEventListener("click",function(e){e.preventDefault();let t=document.getElementById("name").value.trim(),n=document.getElementById("firstname").value.trim(),a=document.getElementById("email").value.trim(),r=document.getElementById("message").value.trim();const c=document.getElementById("alert-contact");0===t.length||0===n.length||0===a.length||0===r.length?(c.className="text-center alert alert-warning",c.innerText="Veuillez renseigner tous les champs"):checkEmail(a)?fetch("https://thomaslaure.fr/contact",{method:"post",headers:{"Content-Type":"application/json",Accept:"application/json"},body:JSON.stringify({message:`${r}\n\n${ucwords(n)} ${t.toUpperCase()}\n${a}`})}).then(e=>{if(!e.ok)throw e;c.className="text-center alert alert-success",c.innerText="Votre message a été envoyé",document.querySelector("#contact-form > form").reset()}).catch(e=>{c.className="text-center alert alert-danger",c.innerText="Un problème a été rencontré, votre message n'a pas pu être envoyé : "+e}):(c.className="text-center alert alert-warning",c.innerText="Adresse mail invalide")});