(function(){function scrollTo(to,behavior,block){const scroll=document.getElementById('scroll-'+to);scroll.onclick=_=>document.getElementById(to).scrollIntoView({behavior:behavior,block:block});} scrollTo("presentation","smooth","start");scrollTo("timeline","smooth","start");scrollTo("skills","smooth","start");scrollTo("realisations","smooth","start");scrollTo("title","smooth","start");scrollTo("contact","smooth","start");function calculateAge(birthdayParam){let today=new Date();const birthday=new Date(birthdayParam);let difference=today.getFullYear()-birthday.getFullYear();if(today.getMonth()-birthday.getMonth()<0||today.getDate()-birthday.getDate()<0&&today.getMonth()===birthday.getMonth()){return difference-1;}else{return difference;}} document.getElementById('age').innerText=calculateAge('1997-06-17');$(window).on("scroll",function(){sessionStorage.scrollPos=$(window).scrollTop();});var init=function(){$(window).scrollTop(sessionStorage.scrollPos||0)};window.onload=init;})();